DROP TABLE IF EXISTS public.customers;

CREATE TABLE public.customers (
    id integer PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
    name text,
    active BOOLEAN NOT NULL DEFAULT 't'
);

